import React from "react";

const AuthContext = React.createContext({
  users: [],
  isLoggedIn: false,
  currentLogin: {
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    money: 0,
    transaction: [],
  },
  onLogout: () => {},
  onLogin: (email, password) => {},
  onSignup: (firstName, lastName, email, password) => {},
  onDeposit: (amount) => {},
  onWithdrawal: (amount) => {},
});

export default AuthContext;
